% I = imread('../data/campus/sun_azirpzgykhrkmqzz.jpg');% filterBank = createFilterBank();% [filterResponses] = extractFilterResponses1(I, filterBank);% figure;% imshow(I);% figure;% temp = rescale(filterResponses(:,:,18), 0, 255);% imshow(uint8(temp));% figure;% temp = rescale(filterResponses(:,:,19), 0, 255);% imshow(uint8(temp));% figure;% temp = rescale(filterResponses(:,:,20), 0, 255);% imshow(uint8(temp));function [filterResponses] = extractFilterResponses(I, filterBank)    I = double(I);    N = size(filterBank,1);        row = 0;    col = 0;    if (ndims(I) == 3)        [L,a,b] = RGB2Lab(I(:,:,1),I(:,:,2),I(:,:,3));        [row, col, ~] = size(I);    else        [L,a,b] = RGB2Lab(I(:,:,1),I(:,:,1),I(:,:,1));        [row, col] = size(I);    end    filterResponses = zeros(row, col, 3*N);    for i = 1:N        filterResponses(:,:,3*(i-1)+1) = conv2(L, filterBank{i}, 'same');        filterResponses(:,:,3*(i-1)+2) = conv2(a, filterBank{i}, 'same');        filterResponses(:,:,3*(i-1)+3) = conv2(b, filterBank{i}, 'same');    endend